import * as React from 'react';
import { SafeAreaView, StyleSheet, 
        TextInput, ScrollView, Image, TouchableOpacity, Animated, Dimensions  } from 'react-native';
import SwipeCard from '../../components/feed/SwipeCard';
import { Text, View } from '../../../components/Themed';
import { RootTabScreenProps } from '../../../types';
import { menuBtn } from '../../../constants/menuBtn';

export default function HomeScreen({ navigation }: RootTabScreenProps<any>) {
  const [text, onChangeText] = React.useState('');
  const [number, onChangeNumber] = React.useState(null);
  const [getBtninfo, onBtnPress] = React.useState(null)
  const [getMenuBtn, setMenuBtn] = React.useState(menuBtn)
  const [swapState, setSwapState] = React.useState(false)
  // const [fadeAnim, setFadeAnim] = React.useState(new Animated.Value(0))
  // const [rotateAnim, setRotateAnim] = React.useState(new Animated.Value(0))
  const [fadeAnim, setFadeAnim] = React.useState(0)
  const [rotateAnim, setRotateAnim] = React.useState('0deg')
  const [getFeedData, setFeedData] = React.useState([
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
    {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')}
  ])
  const win = Dimensions.get('window');

  const setMenubutton=(data:any)=>{
    console.log('adfasd')
    menuBtn.forEach((item:any,index:any) => {
      getMenuBtn[index]={title:item.title, state:index==data?true:false}
    })
    setMenuBtn([...getMenuBtn])
    setFeedData([
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')},
      {name:'Orchad House',price:'Rp. 2.000.000.000 / Year',bedroomNum:'5 Bedroom',bathroomNum:'2 Bathroom',image_url:require('../../../assets/images/Image.png')}
    ])
  }
  const decline = ()=>{
    setSwapState(true)
    setFadeAnim(-100)
    setRotateAnim('350deg')
    // Animated.timing(
    //   fadeAnim,
    //   {
    //     toValue: -100,
    //     duration: 300,
    //     useNativeDriver: true
    //   }
    // ).start();
    // Animated.timing(
    //     rotateAnim,
    //   {
    //     toValue: 1,
    //     duration: 300,
    //     // easing: Easing.linear, // Easing is an additional import from react-native
    //     useNativeDriver: true  // To make use of native driver for performance
    //   }
    // ).start()
      setTimeout(() => {
        getFeedData.pop()
        setFeedData([...getFeedData])
        setSwapState(false)
        setFadeAnim(0)
        setRotateAnim('0deg')
        // setFadeAnim(new Animated.Value(0))
        // setRotateAnim(new Animated.Value(0))
        // setRotateVal('330deg')
    }, 500);
    
    
  }
  // const rotateInterpolate = rotateAnim.interpolate({
  //   inputRange: [0, 1],
  //   outputRange: ['0deg', '-30deg']
  // });
  return (
    <View style={styles.homeview}>
      <View>
        <Text style={styles.title}>Choose house to live</Text>
      </View>
      <View style={{ display: 'flex', alignItems: 'center', flex:1 }}></View>
      <View>
        <ScrollView style={styles.homeBtns} horizontal={true}>
          {getMenuBtn.map((item,index)=>{ return <TouchableOpacity style={{marginBottom:7}} key={index} onPress={e => {e.preventDefault(); setMenubutton(index)}} >
            <Text style={[styles.btnStyle,{backgroundColor:item.state?'#30C0E9':'#F2F3F7',color:item.state?'white':'#ACB7C2'}]} >{item.title}</Text>
          </TouchableOpacity>})}
        </ScrollView>
      </View>
      {/* <View style={{ display: 'flex', alignItems: 'center', flex:1 }}></View> */}
      <View style={{height:win.height-300,position:'relative'}}>
        <Text style={{color:'#FF737F',fontSize:30,borderColor:'#FF737F',borderWidth:2,padding:10,width:'auto',marginLeft:'auto',marginRight:'auto',marginTop:100}}>NONE</Text>
        {getFeedData.map((item,index)=>{
          return <SwipeCard key={index} indexData={
            Object.assign(item,{index,swapState:index==getFeedData.length-1?swapState:false,
                                animationVal:index==getFeedData.length-1?fadeAnim:0,
                                rotateVal:index==getFeedData.length-1?rotateAnim:'0deg',})}/>})}
      </View>
      {/* <View style={{ display: 'flex', alignItems: 'center', flex:1 }}></View> */}
      <View style={{justifyContent:'center',marginLeft:'auto',marginRight:'auto',marginTop:10,marginBottom:'1%'}}>
        <View style={{width:(Dimensions.get('window').height-40)*9/37,flexDirection:'row',}}>
        <TouchableOpacity style={{marginRight:'auto'}} onPress={() => decline()}>
          <Image style={styles.swip_icon} source={require('../../../assets/icons/dislike_icon.png')}/>
        </TouchableOpacity>
        <TouchableOpacity style={{marginLeft:'auto'}} onPress={() => {}}>
          <Image style={styles.swip_icon_1} source={require('../../../assets/icons/like_icon.png')}/>
        </TouchableOpacity> 
        </View>
      </View>
      <View style={{ display: 'flex', alignItems: 'center', flex:1 }}></View>
      <View style={{height:75}}>

      </View>
     
    </View>
  );
}

const styles = StyleSheet.create({


  swip_icon:{
    width:(Dimensions.get('window').height-40)*3/37,
    height:(Dimensions.get('window').height-40)*3/37,
  },
  swip_icon_1:{
    width:(Dimensions.get('window').height-40)*3/37,
    height:(Dimensions.get('window').height-40)*3/37,
    marginLeft:'auto'
  },

  btnStyle:{
    backgroundColor: '#F2F3F7',
    padding: 10,
    paddingLeft:15,
    paddingRight:15,
    borderRadius: 10,
    textAlign: 'center',
    marginRight:15,
    color: '#ACB7C2'
  },
  homeBtns:{
    paddingBottom:5,
  },
  homeview:{
    padding:20,
    flex:1
  },
  title: {
    fontSize: 28,
    fontWeight: '600',
  },
});
