import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Image, Dimensions, TouchableOpacity } from "react-native";
import { DATA } from "../utils";
const win = Dimensions.get('window');
export type CardProps = typeof DATA[0];

export const Card = ({ title, image, id, address, bedroom, bathroom, checked }: CardProps) => {



  return (
    // <View key={id} style={styles.container}>
    //   <View key={id} style={[styles.item, { height: win.width + 20, width: win.width - 40 }]}>
    //     <Image style={[styles.image, { height: win.width - 90, width: win.width - 70 }]} source={{ uri: image }} />
    //     <Text style={styles.title}>{title}</Text>
    //     <Text style={styles.addr_title}>{address}</Text>
    //   </View>
    // </View>
    <View key={id} style={styles.image_card_view}>
      <Image style={styles.image_card} source={{ uri: image }} />
      <View style={{ marginLeft: 15, marginRight: 15 }}>
        <Text style={styles.homeTitleText}>{title}</Text>
        <Text style={[styles.homeTitleText1, { color: '#297EE4', marginTop: 4 }]}>{address}</Text>
        <View style={{ flexDirection: 'row', marginTop: 4 }}>
          <View style={{ flexDirection: 'row' }}>
            <Image style={{ width: 15, height: 15, marginRight: 5 }} source={require('../assets/icons/Vector_(2).png')} />
            <Text style={[styles.homeTitleText1, { width: 80 }]}>{bedroom}&nbsp;Bedroom</Text>
          </View>
          <View style={{ flexDirection: 'row' }}>
            <Image style={{ width: 15, height: 15, marginRight: 5 }} source={require('../assets/icons/Group_1.png')} />
            <Text style={styles.homeTitleText1}>{bathroom}&nbsp;Bathroom</Text>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
  },
  item: {
    borderRadius: 30,
    backgroundColor: "white",
    padding: 15,
    marginVertical: 8,
    marginHorizontal: 8,
    marginBottom: 24,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.8,
    shadowRadius: 10,
    elevation: 2,
  },
  title: {
    fontSize: 18,
    fontWeight: '600',
  },
  addr_title: {
    fontSize: 16,
  },
  image: {
    flex: 1,
    borderRadius: 20,
  },
  swip_icon: {
    width: (Dimensions.get('window').height - 40) * 3 / 37,
    height: (Dimensions.get('window').height - 40) * 3 / 37,
  },
  swip_icon_1: {
    width: (Dimensions.get('window').height - 40) * 3 / 37,
    height: (Dimensions.get('window').height - 40) * 3 / 37,
    marginLeft: 'auto'
  },
  homeTitleArrow: { width: 20, height: 20, marginRight: 10, marginTop: 13 },
  homeTitleText1: { fontSize: 12, fontWeight: '400', color: '#ACB7C2' },
  homeTitleText: { fontSize: 16, fontWeight: '500' },
  homeTitleImage: { width: 42, height: 42, marginRight: 10 },
  homeTitleView: { flexDirection: 'row', width: '100%', marginTop: 10, marginBottom: 20 },
  image_card: {
    width: (Dimensions.get('window').width - 40) - 30,
    height: (Dimensions.get('window').height - 40) * 22 / 37 - 110,
    marginTop: 15,
    marginLeft: 15,
    marginBottom: 5,
    borderRadius: 10,
  },
  image_card_view: {
    height: (Dimensions.get('window').height - 40) * 22 / 37 - 10,
    borderRadius: 15,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.2,
    shadowRadius: 5,
    elevation: 10
  },
});
