import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Image, Dimensions, TouchableOpacity } from "react-native";
import { DATA } from "../utils";
const win = Dimensions.get('window');
export type CardProps = typeof DATA[0];

export const Card = ({ type,title, image, id, address, bedroom, bathroom, checked }: 
    {type:any, title:any, image:any, id:any, address:any, bedroom:any, bathroom:any, checked:boolean}) => {



  return (
    <View key={id} style={styles.container}>
      {type!='default'?<View key={id} style={[styles.item, {padding: 15, height: win.height - 380, width: win.width-50 }]}>
        <Image style={styles.image} source={{ uri: image }} />
        <Text style={styles.title}>{title}</Text>
        <Text style={styles.addr_title}>{address}</Text>
      </View>
      :
      <View key={id} style={[styles.item, { height: win.height - 380, width: win.width-50 }]}>
        <Image style={[styles.image,{width: win.width-50}]} source={{ uri: image }} />
      </View>}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
  },
  item: {
    borderRadius: 30,
    alignItems: "center",
    backgroundColor: "white",
    marginVertical: 8,
    marginHorizontal: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.8,
    shadowRadius: 10,
    elevation: 2,
  },
  title: {
    fontSize: 18,
    fontWeight: '600',
  },
  addr_title: {
    fontSize: 16,
  },
  image: {
    flex: 1,
    borderRadius: 20,
    width: win.width-80,
  },
  swip_icon: {
    width: (Dimensions.get('window').height - 40) * 3 / 37,
    height: (Dimensions.get('window').height - 40) * 3 / 37,
  },
  swip_icon_1: {
    width: (Dimensions.get('window').height - 40) * 3 / 37,
    height: (Dimensions.get('window').height - 40) * 3 / 37,
    marginLeft: 'auto'
  },
});
