import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Image, Dimensions, TouchableOpacity } from "react-native";
import { DATA } from "../utils";
const win = Dimensions.get('window');
export type CardProps = typeof DATA[0];

export const Card = ({ type, title, image, id, address, bedroom, bathroom, checked }:
  { type: any, title: any, image: any, id: any, address: any, bedroom: any, bathroom: any, checked: boolean }) => {



  return <>
    {type != 'default' ? <View key={id} style={styles.container}>
      <View key={id} style={[styles.item,]}>
        <Image style={[styles.image, { margin: 15, height: win.width / 1.3, width: win.width - 80 }]} source={{ uri: image }} />
        <Text style={styles.title}>{title}</Text>
        <Text style={styles.addr_title}>{address}</Text>
      </View>

    </View>
      :
      <View key={id} style={styles.container}>
        <View key={id} style={[styles.item, { height: win.width + 20, width: win.width - 40 }]}>
          <Image style={[styles.image, { height: win.width - 90, width: win.width - 70 }]} source={{ uri: image }} />
          <View style={{ marginHorizontal: 15, marginTop: 10 }}>
            <Text style={styles.homeTitleText}>{title}</Text>
            <Text style={[styles.homeTitleText1, { color: '#297EE4', marginTop: 4 }]}>{address}</Text>
            <View style={{ flexDirection: 'row', marginTop: 4 }}>
              <View style={{ flexDirection: 'row' }}>
                <Image style={{ width: 15, height: 15, marginRight: 5 }} source={require('../assets/icons/Vector_(2).png')} />
                <Text style={[styles.homeTitleText1, { width: 80 }]}>{bedroom}&nbsp;Bedroom</Text>
              </View>
              <View style={{ flexDirection: 'row' }}>
                <Image style={{ width: 15, height: 15, marginRight: 5 }} source={require('../assets/icons/Group_1.png')} />
                <Text style={styles.homeTitleText1}>{bathroom}&nbsp;Bathroom</Text>
              </View>
            </View>
          </View>
        </View>
      </View>
      // <View key={id} style={styles.container1}>
      //   <View key={id} style={[styles.item]}>
      //     <Image style={[styles.image,{width: win.width-60,height:win.width/1.3}]} source={{ uri: image }} />
      //   </View>
      // </View>
    }
  </>
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
  },

  container1: {
    flex: 1,
    alignItems: "center",
    margin: 10
  },

  item: {
    borderRadius: 30,
    alignItems: "center",
    backgroundColor: "white",
    marginVertical: 8,
    marginHorizontal: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.8,
    shadowRadius: 10,
    elevation: 2,
  },

  title: {
    fontSize: 18,
    fontWeight: '600',
  },

  addr_title: {
    fontSize: 16,
  },

  image: {
    flex: 1,
    borderRadius: 20,
    width: win.width - 80,
  },

  swip_icon: {
    width: (Dimensions.get('window').height - 40) * 3 / 37,
    height: (Dimensions.get('window').height - 40) * 3 / 37,
  },

  swip_icon_1: {
    width: (Dimensions.get('window').height - 40) * 3 / 37,
    height: (Dimensions.get('window').height - 40) * 3 / 37,
    marginLeft: 'auto'
  },

  homeTitleText1: {
    fontSize: 12,
    fontWeight: '300',
    color: '#000'
  },

  homeTitleText: {
    fontSize: 16,
    fontWeight: '500'
  },
});
